\relative a' <<
  \new Voice {
    \clef treble
    \time 4/4
    \key d \major

    \repeat volta 2 {
      \partial 4 a4 |
      d8 a a a e' a, fis' a, |
      g' b, b b a' b, g' b, |
      fis' a, a a d a fis' a, |
      e' a, a a cis d e cis |
      \break

      d8 a a a e' a, fis' a, |
      g' b, b b a' b, g' b, |
      fis' a, d a e' b cis a |
    } \alternative { {
      \set Timing.measureLength = #(ly:make-moment 3/4)
      d a fis' a, d4 |
    } {
      \set Timing.measureLength = #(ly:make-moment 4/4)
      d8 a b cis d e fis g |
    } } \break

    \repeat volta 2 {
      fis d d d a d d d |
      fis d d d b d d d |
      g d d d b d g b |
      a a, b cis d e fis g |
      \break

      fis d d d a d d d |
      fis d d d b d d d |
      g e fis d e cis d b |
    } \alternative { {
      cis a b cis d e fis g |
    } {
      cis, a b cis d4
    } } \bar "|."
  }

  \new ChordNames {
    \chordmode {
      s4 |
      d1 | g | d | a |
      d1 | g | d2 e4:m a | d2. | d1 |

      d1 | b:m | g | a:7 |
      d1 | b:m | e4:m d \move-left 1 a/cis g/b | a1:7 | a2:7 d4
    }
  }
>>

\relative f <<
  \new Voice {
    \clef treble
    \key b \minor
    \time 4/4

    \repeat volta 2 {
      \repeat volta 2 {
        b16 fis'8. fis g16 fis8. e16 d8. cis16 |
        a16 e'8. e fis16 e8. d16 cis8. ais16 |
        b16 fis'8. fis g16 fis8. e16 d8. cis16 |
        \tuplet 3/2 { a8 cis e } fis8 ais, b4 b4 |
      } \break

      fis'16 b8. b cis16 d8. cis16 b8. a16 |
      g16 b8. b cis16 d8. cis16 b8. a16 |
      a16 e'8. e fis16 g8. fis16 e8. d16 |
      \tuplet 3/2 { cis8 fis, d } \tuplet 3/2 { b'8 fis d } \tuplet 3/2 { a'8 fis d } \tuplet 3/2 { b'8 fis d } |
      \break

      fis16 b8. b cis16 d8. cis16 b8. a16 |
      g16 b8. b cis16 d8. cis16 b8. a16 |
      a16 e'8. e fis16 g8. fis16 e8. d16 |
      cis16 b a g fis8 ais b4 b |
    }
  }

  \new ChordNames {
    \chordmode {
      b1:m | a2 fis | b1:m | a4 fis b2:m |

      b1:m | g | a | b:m7 |
      b1:m | g | a | a4 fis b2:m |
    }
  }
>>
