\relative f' <<
  \new Voice {
    \clef treble
    \time 4/4
    \key g \minor

    \repeat volta 2 {
      \repeat volta 2 {
        g8. bes16 d,8. g16 f8. a16 c8. f,16 |
        ees8. g16 bes8. ees16 d4 d |
        g,8. bes16 d8. bes16 c16 bes a g f4 |
      } \alternative { {
        ees8. g16 f16 ees8. d4 d |
      } {
        ees8. g16 fis16 d8. g4 g8 a |
      } } \bar "||" \break

      bes8. d16 f8. d16 c16 d ees f g4 |
      f16 g8. a4 f16 g8. a4 |
      bes8. g16 d g8. a8. f16 c f8. |
      fis8. d16 a d8. ees8 d c4 |
      \break

      bes8. d16 f8. d16 c16 d ees f g4 |
      f16 g8. a4 f16 g8. a4 |
      bes8. g16 d g8. ees8. c16 g c8. |
      a8. fis16 d fis8. g8 g, g4 |
    }
  }

  \new ChordNames {
    \chordmode {
      g2:m f | ees d:7 | g:m f | ees4 f d2:7 | ees4 d:7/fis g2:m |
      bes2 c:m | f/c d:m | g2:m f | d1:7 |
      bes2 c:m | f/c d:m | g2:m c:m | d:7 g:m |
    }
  }
>>
