a-strath-jig-for-george.content:
\relative f' <<
  \new Voice {
    \clef treble
    \time 6/8
    \key d \minor

    \partial 8 a8 |
    \repeat volta 2 {
      d4 a8 d4 a8 |
      a'8 g f e f e |
      d4 a8 d4 c8 |
      bes4. a8 bes c |
      \break

      d4 a8 d4 a8 |
      a'8 g f e f e |
      d8 e f e f g |
    } \alternative { {
      f4. f8 e d |
    } {
      f4. f8 e d |
    } } \break

    \repeat volta 2 {
      bes4 g8 bes4 d8 |
      g8 f e d c bes |
      a8 e e cis' e, e |
      d'8 e, e e' e, e |
      \break

      bes'4 g8 bes4 d8 |
      g8 f e d c bes |
      bes8 c d c d e |
    } \alternative { {
      f4. f8 e d |
    } {
      d4. d4
    } } \bar "|."
  }

  \new ChordNames {
    \chordmode {
      s8 |
      d2.:m | a:7 | d:m | g:m |
      d2.:m | a:7 | bes4. c:7 | d2.:m | f |

      g2.:m | s | a:7 | s |
      g2.:m | s | bes4. c:7 | f2. | d2:m
    }
  }
>>

a-strath-reel-for-george.content:
\relative f' <<
  \new Voice {
    \clef treble
    \time 4/4
    \key d \minor

    \partial 4 a4 |
    \repeat volta 2 {
      d4 a8 cis d4 a8 cis |
      a'8 g f e g f e f |
      d4 a8 cis d4 a8 c |
      bes4 a8 bes c a bes c |
      \break

      d4 a8 cis d4 a8 cis |
      a'8 g f e g f e f |
      d8 e f d e f g e |
    } \alternative { {
      f2 f8 e d c |
    } {
      f2 f8 e d c |
    } } \break

    \repeat volta 2 {
      bes8 g bes d g bes, d g |
      g8 f e d c bes a g |
      a8 e a cis e a, cis e |
      g8 cis, e g a e a4 |
      \break

      bes,8 g bes d g bes, d g |
      g8 f e d c bes a bes |
      bes8 c d bes c d e c |
    } \alternative { {
      f2 f8 e d c |
    } {
      d2 d4
    } } \bar "|."
  }

  \new ChordNames {
    \chordmode {
      s4 |
      d1:m | a:7 | d:m | g:m |
      d1:m | a:7 | bes2 c:7 | d1:m | f |

      g1:m | s | a:7 | s |
      g1:m | s | bes2 c:7 | f1 | d2.:m
    }
  }
>>

a-strathspey-for-george.content:
\relative f' <<
  \new Voice {
    \clef treble
    \time 4/4
    \key d \minor

    \repeat volta 2 {
      \repeat volta 2 {
        a16 d8. d a16 a'8. g16 f e8. |
        a,16 d8. d c16 bes4 a8 g |
        a16 d8. d a16 a'8. g16 f e8. |
      } \alternative { {
        \tuplet 3/2 {d8 e f} \tuplet 3/2 {e8 f g} f4 e8 d |
      } {
        \tuplet 3/2 {d8 e f} \tuplet 3/2 {e8 f g} f4 f16 e d c |
      } } \break

      \repeat volta 2 {
        bes16 g8. g' f16 e8. d16 c bes8. |
        a8. e16 cis'8 e, d' e, e' e, |
        bes'16 g8. g' f16 e8. d16 c bes8. |
      } \alternative { {
        \tuplet 3/2 {bes8 c d} \tuplet 3/2 {c8 d e} f4 f16 e d c |
      } {
        \tuplet 3/2 {bes8 c d} \tuplet 3/2 {c8 d e} d4 d |
      } }
    }
  }

  \new ChordNames {
    \chordmode {
      d2:m a:7 | d2:m g:m | d:m a:7 | bes4 c:7 d2:m | bes4 c:7 f2 |
      g1:m | a:7 | g:m | bes4 c:7 f2 | bes4 c:7 d2:m |
    }
  }
>>

