cleansing-tea.content:
\relative f'' <<
  \new Voice {
    \clef treble
    \time 4/4
    \key a \mixolydian

    \repeat volta 2 {
      cis8 b cis a b cis a b |
      <a g'>4. <a fis'>8 ~ <a fis'>4 <a fis'>4 |
      cis8 b cis a b cis a b |
      <a e'>4 <a d> <a cis> <a b> |
      \break

      cis8 b cis a b cis a b |
      <a g'>4. <a fis'>8 ~ <a fis'>4 <a fis'>4 |
      fis'8 g a b a fis d e |
    } \alternative { {
      <d fis>4. <cis e>8 ~ <cis e>4 <cis e>4 |
    } {
      <d fis>4. <cis e>8 ~ <cis e>4 <cis e>8 fis |
    } } \break

    \repeat volta 2 {
      g8 fis g d e g d e |
      g4 fis e d |
      fis8 e fis d e fis d e |
      <d fis>4. <cis e>8 ~ <cis e>4 <cis e>8 fis |
      \break

      g8 fis g d e g d e |
      g4 fis e d |
      <d fis>4 <d fis>8 <d fis>8 ~ <d fis>4 <d fis>4 |
    } \alternative { {
      <cis e>4. <cis e>8 ~ <cis e>4 <cis e>8 fis |
    } {
      <a, cis e>2 <a cis e a>2 |
    } } \bar "|."
  }

  \new ChordNames {
    \chordmode {
      a1 | g | a | a:7 |
      a1 | g | d | d2 a:7 | d a:7 |
      g1 | s | d | d2 a:7 |
      g1 | s | d | a:7 | a2 a:7 |
    }
  }
>>

la-ville-lumiere.content:
\relative a' <<
  \new Voice {
    \clef treble
    \time 4/4
    \key d \major

    \repeat volta 2 {
      \partial 4 a4 |
      d8 a a a e' a, fis' a, |
      g' b, b b a' b, g' b, |
      fis' a, a a d a fis' a, |
      e' a, a a cis d e cis |
      \break

      d8 a a a e' a, fis' a, |
      g' b, b b a' b, g' b, |
      fis' a, d a e' b cis a |
    } \alternative { {
      \set Timing.measureLength = #(ly:make-moment 3/4)
      d a fis' a, d4 |
    } {
      \set Timing.measureLength = #(ly:make-moment 4/4)
      d8 a b cis d e fis g |
    } } \break

    \repeat volta 2 {
      fis d d d a d d d |
      fis d d d b d d d |
      g d d d b d g b |
      a a, b cis d e fis g |
      \break

      fis d d d a d d d |
      fis d d d b d d d |
      g e fis d e cis d b |
    } \alternative { {
      cis a b cis d e fis g |
    } {
      cis, a b cis d4
    } } \bar "|."
  }

  \new ChordNames {
    \chordmode {
      s4 |
      d1 | g | d | a |
      d1 | g | d2 e4:m a | d2. | d1 |

      d1 | b:m | g | a:7 |
      d1 | b:m | e4:m d \move-left 1 a/cis g/b | a1:7 | a2:7 d4
    }
  }
>>

solstice-jig.content:
\relative f' <<
  \new Voice {
    \clef treble
    \time 6/8
    \key a \major

    \partial 8 e8 |
    \repeat volta 2 {
      a4 a8 a gis fis |
      e fis e cis d e |
      fis4 fis8 d e fis |
      e4. cis4 e8 |
      \break

      d4 d8 b cis d |
      cis4 e8 a4 gis8 |
    } \alternative { {
      fis4 fis8 fis gis a |
      b4. e,4 gis8 |
    } {
      fis4 fis8 gis a b |
      a4. ~ a4 gis8 |
    } } \break

    \repeat volta 2 {
      fis4 fis8 b gis fis |
      e8 fis e a4 e8 |
      d4 d8 gis e d |
      cis8 d dis e4 a8 |
      \break

      fis4 fis8 b gis fis |
      e8 fis gis a4 e8 |
      d8 e fis e fis gis |
    } \alternative { {
      a4. a4 g8 |
    } {
      a4. a4
    } } \bar "|."
  }

  \new ChordNames {
    \chordmode {
      s8 |
      a4. d | e a | d e:7 | a a/cis |
      b4.:m e/gis | a a/cis | b b:7/dis | e2. | b4. e | a2. |

      d4. e | a/cis fis:m | b:m e | a a/cis |
      d4. e | a/cis fis:m | d e | a a:7 | a
    }
  }
>>

